sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

	var tradeAudExist = [];

	var prevUpdatedTime = new GlideDateTime(previous.sys_updated_on).getDisplayValue();

    var auditObject = {
        "previousPriority": previous.priority.getDisplayValue(),
        "currentPriority": current.priority.getDisplayValue(),
        "assignedTo": current.assigned_to.getDisplayValue(),
        "channel": current.contact_type.getDisplayValue(),
        "account": current.account.getDisplayValue(),
        "contract": current.contract.getDisplayValue(),
        "contact": current.contact.getDisplayValue(),
        "product": current.product.getDisplayValue(),
        "previousUpdatedTimeStamp": prevUpdatedTime,
		"resolvedBy": current.resolved_by.getDisplayValue(),
		"resolutionCode": current.resolution_code.getDisplayValue()

    };

    if (current.x_938076_now_utils_audit_history) {
        tradeAudExist = JSON.parse(current.x_938076_now_utils_audit_history);
    } else {
        tradeAudExist = [];
    }
    tradeAudExist.push(auditObject);
	//gs.info('tradeAudExist2' +  tradeAudExist.toString());
	//tradeAudExist.reverse(); if reverse needed top to bottom update
    current.x_938076_now_utils_audit_history = JSON.stringify(tradeAudExist);


})(current, previous);
