sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {


	getAuditHistory(current.sys_id);

    function getAuditHistory(recordSysId) {
        var gr = new GlideRecord('sys_audit');
        gr.addQuery('documentkey', recordSysId); // Filter by the Sys ID of the record
        gr.orderByDesc('sys_created_on'); // Order by most recent changes
		gr.setLimit(1);
        gr.query();

        var historyHtml = '<style>table, th, td {border: 1px solid black;border-collapse: collapse; text-align: center; vertical-align: middle;}</style>   <table class="table table-bordered" style="width:100%">';

        historyHtml += '<thead><tr><th>Change Date</th><th>Field</th><th>Old Value</th><th>New Value</th><th>Changed By</th></tr></thead><tbody>';

        while (gr.next()) {
            var createdDate = gr.sys_created_on.getDisplayValue();
            var fieldName = gr.fieldname;
            var oldValue = previous.priority.getDisplayValue() ? previous.priority.getDisplayValue() : 'N/A';
            var newValue = current.priority.getDisplayValue() ? current.priority.getDisplayValue() : 'N/A';
            var changedBy = gr.user;

            historyHtml += '<tr>';
            historyHtml += '<td>' + createdDate + '</td>';
            historyHtml += '<td>' + fieldName + '</td>';
            historyHtml += '<td>' + oldValue + '</td>';
            historyHtml += '<td>' + newValue + '</td>';
            historyHtml += '<td>' + changedBy + '</td>';
            historyHtml += '</tr>';
        }

        historyHtml += '</tbody></table>';

		current.work_notes = '[code]' + historyHtml + '[/code]';
    }

})(current, previous);
