sys_script_include.script:
var getEmailAddress = Class.create();
getEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    fetchEmail: function() {

        var usrEmail = [];

        var getEmail = new GlideRecord('sys_user');
        getEmail.addEncodedQuery('email!=NULL');
        getEmail.query();
        while (getEmail.next()) {

            var key = getEmail.email.toString();
            var value = getEmail.email.toString();
            var optionCreate = key + ':' + value;

            if (usrEmail.indexOf(optionCreate) === -1) {
                usrEmail.push(optionCreate + '');
            }
        }


        return usrEmail.toString();

    },


    type: 'getEmailAddress'
});
