sys_script_include.script:
var getGeminiResponse = Class.create();
getGeminiResponse.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    /*Script Usage
	var geminiresponseObj  = new getGeminiResponse().responseFromGemini('About India');
	gs.info(geminiresponseObj);
	*/


    responseServerFromGemini: function(userPrompt) {
        try {
            var r = new sn_ws.RESTMessageV2('x_938076_now_utils.Gemini Integration', 'Gemini Response');
            r.setStringParameterNoEscape('prompt', userPrompt);
            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            var geminiresponseBody = JSON.parse(responseBody);
            var geminiresponseAnswer = geminiresponseBody.candidates[0].content.parts[0].text;
            return geminiresponseAnswer;

        } catch (ex) {
            var message = ex.message;
        }
    },



    responseClientFromGemini: function() {
        try {

			var userPrompt = this.getParameter('sysparm_prompt');
            var r = new sn_ws.RESTMessageV2('x_938076_now_utils.Gemini Integration', 'Gemini Response');
            r.setStringParameterNoEscape('prompt', userPrompt);
            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            var geminiresponseBody = JSON.parse(responseBody);
            var geminiresponseAnswer = geminiresponseBody.candidates[0].content.parts[0].text;

			//Updating the record
			var gr = new GlideRecord('x_938076_now_utils_gemini_response');
			gr.get('2481f80683995210c6695855eeaad3d4');
			gr.setValue('response', '');
			gr.response = geminiresponseAnswer;
			gr.update();
			//updating the record

            return geminiresponseAnswer;

        } catch (ex) {
            var message = ex.message;
        }
    },
    type: 'getGeminiResponse'
});
