sysauto_script.script:
var caseRec = new GlideRecord("sn_customerservice_case");
caseRec.addEncodedQuery('active=true');
caseRec.query();
while (caseRec.next()) {
    var todayDate = caseRec.sys_created_on;
    var currDate = new GlideDateTime(todayDate);
    currDate.addDays(5);
    var sched = new GlideSchedule("cmn_schedule");
    sched.addEncodedQuery('name=8-5 weekdays');
    sched.query();
    if (sched.next()) {
        if (sched.isInSchedule(currDate)) {
            gs.info(" Within schedule");
            caseRec.state = "3"; // close Complte

        }
    }

    caseRec.update();
}


