(function executeRule(current, previous /*null when async*/ ) {

    var templateGr = new GlideRecord('sn_doc_html_template');
    if (templateGr.get(current.getValue('document_template'))) {
        var body = templateGr.getValue('html_script_body');
        var previousName = previous.getValue('name');
        previousName = previousName.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
        var previousSignatureTokenRegex = new RegExp('\\${signature:' + previousName + '}', 'g');
        var newName = current.getValue('name');
        if (!gs.nil(previousName) && !gs.nil(newName)) {
            var newSignatureToken = '${signature:' + newName + '}';
            body = body.replace(previousSignatureTokenRegex, newSignatureToken);
            templateGr.setValue('html_script_body', body);
            templateGr.update();
        }
    }

})(current, previous);
