x_938076_now_utils_now_utils_business_ru.script:
validate();

function validate() {
 // validate unicity
 var gr = new GlideRecord('sm_m2m_group_dependency');
 gr.addQuery('dispatch_group', current.dispatch_group);
 gr.addQuery('assignment_group', current.assignment_group);
 gr.query();
 if (gr.hasNext()) {
  gs.addErrorMessage(gs.getMessage('The selected dispatch group already covers the selected assignment group'));
  current.setAbortAction(true);
  return;
 }

 var sc = new sn_sm.SMConfiguration();

 // validate dispatch group
 if (!sc.hasGroupType(current.dispatch_group.type, 'dispatch')) {
  gs.addErrorMessage(gs.getMessage('{0} is not a valid dispatch group', current.dispatch_group.name));
  current.setAbortAction(true);
  return;
 }

 // validate assignment group
 if (!sc.hasGroupType(current.assignment_group.type, 'work') && GlidePluginManager.isActive('com.snc.fsm_contractor_management') && !sc.hasGroupType(current.assignment_group.type, 'vendor')) {
   gs.addErrorMessage(gs.getMessage('{0} is not a valid assignment group', current.assignment_group.name));
   current.setAbortAction(true);
   return;

 }

 // validate dispatch group and assignment group part of same app
 var assignmentGroupTypes = current.assignment_group.type.toString().split(',');
 var dispatchGroupTypes = current.dispatch_group.type.toString().split(',');

 for(var i = 0; i < assignmentGroupTypes.length; i++) {
  for(var j = 0; j < dispatchGroupTypes.length; j++) {
   var config = new GlideRecord('sm_config');
   config.addQuery('dispatch_group_type', dispatchGroupTypes[j]);
   var query = config.addQuery('work_group_type', assignmentGroupTypes[i]);
   if(GlidePluginManager.isActive('com.snc.fsm_contractor_management'))
    query.addOrCondition('vendor_group_type', assignmentGroupTypes[i]);
   config.query();
   if (config.hasNext())
    return;
  }
 }

 // no combination of work group types and dispatch group types can be used
 // to find a configuration...
 gs.addErrorMessage(gs.getMessage('Failed to find an SM configuration that would allow the selected dispatch group to cover the selected assignment group'));
 current.setAbortAction(true);
 return;
}
