x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 if (!(new GlidePluginManager().isActive('com.glide.platform_ml')))
  return;

 var isChanged = false;
 var hrAIConfiguration = new GlideRecord('sn_hr_core_ai_configuration'); //configuration table 
 hrAIConfiguration.addQuery('use_case', hr.CASE_CATEGORIZATION);
 hrAIConfiguration.addQuery('sys_domain', current.sys_domain);
 hrAIConfiguration.query(); 
 if (!hrAIConfiguration.next()) {
  hrAIConfiguration = new GlideRecord('sn_hr_core_ai_configuration');
  hrAIConfiguration.addQuery('use_case', hr.CASE_CATEGORIZATION);
  hrAIConfiguration.addQuery('sys_domain', 'global');
  hrAIConfiguration.query();
  if (!hrAIConfiguration.next())
   return;
 }

 var solutionDefinition = new GlideRecord('ml_capability_definition_base');
 if(!solutionDefinition.get(hrAIConfiguration.solution_capability_definition))
  return;

 var fieldlist = solutionDefinition.getValue('fields');
 var fields = fieldlist.split(',');
 for (var i = 0; i < fields.length; i++) {
  if (current.getValue(fields[i]) != previous.getValue(fields[i])) {
   isChanged = true;
   break;
  }
 }

 if (isChanged && !current.transferred_from) {
  var domain = current.sys_domain; 
  var hrCaseAjax = new sn_hr_core.hr_CaseAjax();
  var predictions = hrCaseAjax.getHRCasePredictions(current, domain, hr.CASE_CATEGORIZATION);

  if (predictions) {
   if (new sn_hr_core.hr_CaseCreation().hasAccessForService(current.subject_person, true, predictions.service_id))
    current.predicted_hr_service = predictions.service_id;
   else
    current.predicted_hr_service = 'NULL';
  } else
   current.predicted_hr_service = 'NULL';
 }
})(current, previous);
