x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 if (current.isNewRecord()) {
  var visitorId = '';

  if (current.first_name) {
   visitorId = current.first_name;
  }

  if (current.middle_name) {
   visitorId += (visitorId ? "." + current.middle_name : current.middle_name);
  }

  if (current.last_name) {
   visitorId += (visitorId ? "." + current.last_name : current.last_name);
  }
  var visitorIdToInsert = visitorId.toLowerCase();
  var ga = new GlideAggregate("sn_imt_core_visitor");
  var countStr = '';
  ga.addEncodedQuery("visitor_nameSTARTSWITH" + visitorIdToInsert);
  ga.addAggregate('COUNT');
  ga.query();
  if (ga.next()) {
   countStr = ga.getAggregate('COUNT');
  }
  var count = parseInt(countStr);

  if (!isNaN(count) && count > 0)
   visitorIdToInsert = visitorIdToInsert + (count + 1);
  current.visitor_name = visitorIdToInsert;
 }
})(current, previous);
