x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) { 
 var offeringMetrics = new GlideAggregate('service_offering_metric_data');
 offeringMetrics.addQuery('performance_metric', current.getUniqueValue());
 offeringMetrics.addQuery('service_offering.parent.spm_service_portfolio', current.spm_service_portfolio); 
 offeringMetrics.addQuery('weight_value', '>', 0);
 offeringMetrics.addAggregate('COUNT');
 offeringMetrics.query();
 if (offeringMetrics.next() && offeringMetrics.getAggregate('COUNT') > 0) {
  gs.addErrorMessage(gs.getMessage('{0} cannot be removed from performance calculation, because there are offerings under {1} portfolio with a weight greater than 0. All service owners need to adjust their offering metric weights to zero for this metric before it can be removed.', [current.getDisplayValue(), current.spm_service_portfolio.getDisplayValue()]));
  current.setAbortAction(true);
 }

})(current, previous);
