x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Unique name validation
    var errorMsgs = [];
    var abort = false;
    var entityTypeGr, message;
    entityTypeGr = new GlideRecord(WPConstants.TABLES.ENTITY_TYPE);
    entityTypeGr.addQuery('name', current.getValue('name'));
    entityTypeGr.addQuery('sys_id', '!=', current.getValue('sys_id'));
    entityTypeGr.setLimit(1);
    entityTypeGr.query();
    if (entityTypeGr.hasNext()) {
        abort = true;
        message = gs.getMessage('Entity Type name already exists. Please choose a unique name.');
        errorMsgs.push(message);
    }

    // Unique order validation
    entityTypeGr = new GlideRecord(WPConstants.TABLES.ENTITY_TYPE);
    entityTypeGr.addQuery('order', current.getValue('order'));
    entityTypeGr.addQuery('sys_id', '!=', current.getValue('sys_id'));
    entityTypeGr.setLimit(1);
    entityTypeGr.query();
    if (entityTypeGr.hasNext()) {
        abort = true;
        message = gs.getMessage('Entity Type order already exists. Please choose a unique order.');
        errorMsgs.push(message);
    }

    // Unique Entity mapping column validation
    entityTypeGr = new GlideRecord(WPConstants.TABLES.ENTITY_TYPE);
    entityTypeGr.addQuery('entity_mapping_column', current.getValue('entity_mapping_column'));
    entityTypeGr.addQuery('sys_id', '!=', current.getValue('sys_id'));
    entityTypeGr.setLimit(1);
    entityTypeGr.query();
    if (entityTypeGr.hasNext()) {
        abort = true;
        message = gs.getMessage('Entity Type entity mapping column already exists. Please choose a unique entity mapping column.');
        errorMsgs.push(message);
    }

    if (abort) {
        current.setAbortAction(true);
        for (var i = 0; i < errorMsgs.length; i++) {
            gs.addErrorMessage(errorMsgs[i]);
        }
    }

})(current, previous);
