x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 var name = current.street;
 if (current.city !== null)
  name += "," + current.city;
 if (current.state !== null)
  name += "," + current.state; 
 var mainLoc = new GlideRecord("cmn_location");
 mainLoc.addQuery('consumer',current.sys_id);
 mainLoc.addQuery('primary','true');
 mainLoc.query();
 if(!mainLoc.next()){
  //insert new main address for the consumer
  mainLoc.initialize();
  mainLoc.setValue('name',name);
  mainLoc.setValue('consumer',current.sys_id);
  mainLoc.setValue('primary','true');
  mainLoc.setValue('street',current.street);
  mainLoc.setValue('state',current.state);
  mainLoc.setValue('zip',current.zip);
  mainLoc.setValue('country',current.country);
  mainLoc.setValue('city',current.city);
  mainLoc.insert();
 }else{
  if(current.street != mainLoc.getValue('street') )
   mainLoc.setValue('street',current.street);
  if(current.state != mainLoc.getValue('state') )
   mainLoc.setValue('state',current.state);
  if(current.zip != mainLoc.getValue('zip') )
   mainLoc.setValue('zip',current.zip);
  if(current.country != mainLoc.getValue('country') )
   mainLoc.setValue('country',current.country);
  if(current.city != mainLoc.getValue('city') )
   mainLoc.setValue('city',current.city);

  mainLoc.update();  
 }



})(current, previous);
