// Business Rule: SLA Breach Notifications
// Table: [Specify the relevant table where SLAs are defined, e.g., incident]

(function executeRule(current) {

    var gr = new GlideRecord('task_sla');
    gr.addEncodedQuery('task.sys_class_name=dmn_demand_task^sla.sys_name=WF - Demand Task');
    gr.addQuery('task', current.sys_id);
    gr.setLimit(1);
    gr.query();
    if (gr.next()) {
        // Check if the SLA is breached
        if (gr.has_breached) {

            // Calculate the time difference between now and SLA breach
            var breachTime = new GlideDateTime(gr.sla.breach_time);
            var currentTime = new GlideDateTime();
            var timeDifference = new GlideDuration(breachTime.getNumericValue() - currentTime.getNumericValue());

   gs.addInfoMessage(timeDifference);

            // Calculate the intervals in seconds (24 hours and 2 days)
            var interval24Hrs = 24 * 60 * 60;
            var interval2Days = 2 * interval24Hrs;

            // Check if the SLA breach is more than 24 hours ago
            if (timeDifference.getNumericValue() >= interval24Hrs) {
                // Trigger notification for the 24-hour breach
                gs.eventQueue('sla.breach.24hrs', current, 'incident');
            }

            // Check if the SLA breach is more than 2 days ago
            if (timeDifference.getNumericValue() >= interval2Days) {
                // Trigger notification for the 2-day breach
                gs.eventQueue('sla.breach.2days', current, 'incident');
            }
        }

    }

})(current);
