x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    var message = "";
    var resourcePlan = current.resource_plan.getRefRecord();
    var requestedAllocation = new RequestedAllocation(current);
    if (resourcePlan.plan_type == 'operational_work') {
        current.fte = '';
        current.man_days = '';
        if (current.percent_capacity.changes()) {
            if (current.percent_capacity < 0) {
                message = gs.getMessage("The request cannot be processed because percent capacity cannot be negative. Enter a positive value, and try again");
            } else {
                requestedAllocation.calculateAndSetRequestedHoursFromPercentCapacity();
            }
        } else if (current.requested_hours.changes()) {
            if (current.requested_hours < 0) {
                message = gs.getMessage("The request cannot be processed because planned hours cannot be negative. Enter a positive value, and try again");
            } else {
                requestedAllocation.calculateAndSetPercentCapacityFromRequestedHours();
            }
        }

    } else if (resourcePlan.plan_type == 'task') {
        current.percent_capacity = '';
        if (current.fte.changes()) {
            if (current.fte < 0) {
                message = gs.getMessage("The request cannot be processed because FTE cannot be negative. Enter a positive value, and try again");
            } else {
                requestedAllocation.calculateAndSetManDaysAndRequestedHoursFromFte();
            }

        } else if (current.man_days.changes()) {
            if (current.man_days < 0) {
                message = gs.getMessage("The request cannot be processed because person days cannot be negative. Enter a positive value, and try again");
            } else {
                requestedAllocation.calculateAndSetFteAndRequestedHoursFromManDays();
            }
        } else if (current.requested_hours.changes()) {
            if (current.requested_hours < 0) {
                message = gs.getMessage("The request cannot be processed because planned hours cannot be negative. Enter a positive value, and try again");
            } else {
                requestedAllocation.calculateAndSetManDaysAndFteFromRequestedHours();
            }
        }
    }
    if (message.length > 0) {
        current.setAbortAction(true);
        gs.addErrorMessage(message);
    }

})(current, previous);
