(function executeRule(current, previous /*null when async*/ ) {

    var spotlightGroupName = GlideStringUtil.escapeHTML(current.getValue('name'));
    var spotlightGroupDomainName = new GlideDomainDisplay(current.getValue('sys_domain')).IDToDisplay();
    var loginDomainName = new GlideDomainDisplay(SNC.PADomainUtils.getCurrentUserDomain()).IDToDisplay();

    var message = gs.getMessage("The {0} Spotlight Group is in the {1} domain, but you are in the {2} domain.", [spotlightGroupName, spotlightGroupDomainName, loginDomainName]);
 var messageSwitchDomain = gs.getMessage("To edit this Spotlight Group, switch to the {0} domain.", spotlightGroupDomainName);

 if (gs.hasRole(gs.getProperty('glide.ui.domain_picker.role', 'itil')))
  message += ' ' + messageSwitchDomain;

    var messageNonGlobal = gs.getMessage("To edit this Spotlight Group within the {0} domain, click Copy to Domain and copy this Spotlight Group to the {0} domain.", loginDomainName);

    if (gs.hasRole('pa_spotlight_copy_domain') && loginDomainName != 'global' && loginDomainName.indexOf(spotlightGroupDomainName) == 0)
        message += ' ' + messageNonGlobal;

    gs.addInfoMessage(message);

})(current, previous);
