x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 if(!(current.integrating_system=='adobe_sign' && current.hr_task_type=='action_url')){
  new hr_Task().updateAttachments(current);
 }

 var tasks = new GlideRecord('sn_hr_core_task');
 tasks.addQuery('parent', current.parent.sys_id);
 tasks.addActiveQuery();
 tasks.query();
 var activeTasks = tasks.getRowCount();

 while (tasks.next()) {
  // Check if the rest of the tasks for this case are one of:
  // Closed Complete=3, Closed Incomplete=4, Cancelled=7 or this task
  if (tasks.state == '3' || tasks.state == '4' || tasks.state == '7' 
   || tasks.sys_id == current.sys_id) 
   activeTasks--;
 }

 // Fire the event to send all tasks closed notification
 if(activeTasks == 0) {
  gs.eventQueue('sn_hr_core_task.allClosed', current, current.parent.number);
 }

})(current, previous);
