x_938076_now_utils_now_utils_business_ru.script:
(function() {
    if (previous.state != '10') {
        /*
         * This condition prevents happening of 'Open -> Awaiting Info -> Open' state change in single transaction.
         * This could happen when user has some fulfiller capabilities on case along with case_contributor_creator role and is Requesting Info.
         */
        if ((
                !gs.hasRole('sn_customerservice.customer') &&
                !gs.hasRole('sn_customerservice.consumer') && gs.hasRole('sn_customerservice.case_contributor_creator')
            ) || gs.hasRole('sn_customerservice.unified_consumer')) {
            var util = new global.CSManagementUtils();
            if (util.checkIfLoggedInUserCanFulfillCase(current)) {
                /*
                 * If session user is both Fulfiller and Contributor, case can be moved to Open using 'Open Case' UI action. 
                 * Moving state automatically on commenting will be confusing in that scenario.  
                 * Also, if a session user (for example - agent) who is also an internal unified consumer comments on a case from the portal, 
     * acting as a consumer, then the state change should be blocked as we are not currently supporting this use case [Internal Agent as a consumer].
                 */
                return;
            }
        }
        current.state = 10;
    }
})();
