/**
 * Return the list of variables which are Boolean fields in a conversation
 * 
 * We have to find the fields which are Booleans so we can set up Decision
 * Outcomes based on true/false value for this variable.
 */
function sys_cb_decision_var_boolGetChoices() {

 // if we don't have current, we can't figure out the variable table
 if (typeof current == "undefined" || JSUtil.nil(current) || !current || JSUtil.nil(current.sys_id)) {
  gs.log("Error generating Topic boolean-variable choices: current record is undefined or empty");
  return;
 }

 //answer should be automatically defined in our context when this function is called
 if (!answer) {
  gs.log("Error generating Topic boolean-variable choices: answer is undefined");
  return;
 }
 var variable_table = current.getValue("variable_table");
 var boolean_variables = new TopicVariableAsChoice();

 var choices = boolean_variables.getBooleans(variable_table);

 for (var i=0; i< choices.length; i++) {
  var choice = choices[i];
  answer.add(choice.value, choice.label);
 } 
}
