x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 // check if this is a codesigning certificate attachment.
 var codeSigningKeyUtil = new sn_kmf.CodeSigningKeyCertUtil();
 if (!codeSigningKeyUtil.isCodeSigningCertAttachmentWithDocId(current.table_name, current.table_sys_id)) {
  return; // pass
 }

 // the user who has sec_admin and crypto manage can import the codesigning keypair and can modify the certificate attachment.
 if (gs.hasRole("security_admin") && gs.hasRole("sn_kmf.cryptographic_manager")) {
  return; // pass
 }
 // abort if the user has no security admin or sn_kmf.cryptographic manager role
 gs.addErrorMessage(gs.getMessage("Require security admin and sn_kmf.cryptographic manager roles to delete/update certificate on sys_attachment table. This action is aborted"));
 current.setAbortAction(true);

})(current, previous);
