function updateResourcePlan(){
 var resourcePlan = new GlideRecord('resource_plan');
 if(resourcePlan.isValid()){
  resourcePlan.addQuery('task',current.sys_id);
  resourcePlan.query();
  while(resourcePlan.next()){
   resourcePlan.setValue('task',current.project);
   resourcePlan.setValue('sys_domain',current.project.sys_domain);
   resourcePlan.setValue('top_task', current.project.top_task);
   resourcePlan.update();
  }
 }
}

function updateResourceAggregates(resourceAllocRec, taskId) {
 var rmAggHelper = new RMAggregateHelper();
 var userId = resourceAllocRec.getValue('user');
 var startDate = resourceAllocRec.getValue('start_date');
 var endDate = resourceAllocRec.getValue('end_date');
 var categories = ['project_confirmed', 'project_allocated'];
    rmAggHelper.updateAllAggregatesAsync(userId, startDate, endDate, categories, taskId);
}

function updateResourceAllocations(){
 var resourceAlloc = new GlideRecord('resource_allocation');
 if(resourceAlloc.isValid()){
  resourceAlloc.addQuery('task',current.sys_id);
  resourceAlloc.query();
  while(resourceAlloc.next()){
   resourceAlloc.setValue('task',current.project);
   resourceAlloc.setValue('sys_domain',current.project.sys_domain);
   resourceAlloc.update();
   updateResourceAggregates(resourceAlloc, current.sys_id);
   updateResourceAggregates(resourceAlloc, current.project);
  }
 }
}

function updateResourceEvents(){
 var resourceEvent = new GlideRecord('resource_allocation_daily');
 if(resourceEvent.isValid()){
  resourceEvent.addQuery('task',current.sys_id);
  resourceEvent.addNullQuery('allocation');
  resourceEvent.addQuery('actual_hours', '=', 0);
  resourceEvent.query();
  var helper = new RMAggregateHelper();
  while(resourceEvent.next()){
   resourceEvent.setValue('task',current.project);
   resourceEvent.setValue('top_task', current.project.top_task);
   resourceEvent.setValue('name',current.project.number);
   resourceEvent.setValue('sys_domain',current.project.sys_domain);
   resourceEvent.setWorkflow(false);
   resourceEvent.update();

   var type = resourceEvent.getValue('type');
   helper.updateFromAllocationDailies(resourceEvent.getValue('user'), resourceEvent.getValue('date'), resourceEvent.getValue('date'), type, current.getValue('sys_id'));
   helper.updateFromAllocationDailies(resourceEvent.getValue('user'), resourceEvent.getValue('date'), resourceEvent.getValue('date'), type, current.getValue('project'));
  }
 }
}
