x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
    if (gs.nil(current.parent) && !gs.nil(current.root)) {
        removeRootIB(current);
    } else {
        updateRootIB(current);
    }
    //remove the root
    function removeRootIB() {
        if (current.operation() != "update") {
            gs.addErrorMessage(gs.getMessage('Invalid data; root added without its parent install base item.'));
            current.setAbortAction(true);
        } else if (current.operation() == "update") {
            current.root = current.parent;
        }
    }
    //update the root
    function updateRootIB() {
        var parentGr = new GlideRecord(global.CSMBaseConstants.INSTALL_BASE_ITEM);
        if (parentGr.get(current.parent)) {
            if (validateRootIB(current, parentGr)) {
                current.setAbortAction(true);
            } else {
                if (gs.nil(parentGr.root)) {
                    current.root = parentGr.sys_id;
                } else {
                    current.root = parentGr.root;
                }
            }
        } else {
            gs.addErrorMessage(gs.getMessage('Invalid parent install base item.'));
            current.setAbortAction(true);
        }
    }
    //validate rootIb if populated.
    function validateRootIB(current, parentGr) {
        var currentRoot = current.root,
            abortAction = false;
        if (gs.nil(currentRoot)) {
            return abortAction;
        }
        if (currentRoot == current.sys_id) {
            gs.addErrorMessage(gs.getMessage('The current "install base item" root field should not be self-referencing.'));
            abortAction = true;
        } else
        if (!gs.nil(parentGr.root)) {
            if (parentGr.root != currentRoot) {
                gs.addErrorMessage(gs.getMessage('Root field of install base item should match root field of parent install base item.'));
                abortAction = true;
            }
        } else if (parentGr.sys_id != currentRoot) {
            gs.addErrorMessage(gs.getMessage('Root field of install base item should match root field of parent install base item.'));
            abortAction = true;
        }
        return abortAction;
    }
})(current, previous);
