(function executeRule(current, previous /*null when async*/ ) {
    var htmlValidator = new HtmlTemplateUtils();
    var body = htmlValidator.validateTemplate(current.html_script_body, current.table, current.sys_id);

    var unEvaluatedVariables = htmlValidator.unEvaluatedVariables;
    var inaccessibleVariables = htmlValidator.inaccessibleVariables;
    if (unEvaluatedVariables.length == 0 && inaccessibleVariables.length == 0)
        return;
    // Continue with error handling
    var errStr = '';
    if (unEvaluatedVariables && unEvaluatedVariables.length > 0) {
        errStr = gs.getMessage('The following variables are not valid or defined: {0}', unEvaluatedVariables.join(','));
        gs.addErrorMessage(errStr);
    }

    if (inaccessibleVariables && inaccessibleVariables.length > 0) {
        errStr = gs.getMessage('The following variables are not accessible based on your permissions: {0}', inaccessibleVariables.join(','));
        gs.addErrorMessage(errStr);
    }
    current.setAbortAction(true);
})(current, previous);
