x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    var requestedItemUtils = new LFRequestedItemUtils(current);
    var currentStateLRITM = requestedItemUtils.getCurrentState();

    if (currentStateLRITM != requestedItemUtils.states.Open) {
        gs.addErrorMessage(gs.getMessage("Requested item not in Open state cannot be added to or removed from a project."));
        current.setAbortAction(true);
    } else {
        var prevProjectId = previous.getValue("localization_project");

        var prevProjectUtils = new LFProjectUtils(prevProjectId);
        if (prevProjectUtils.hasValidRecord()) {
            var prevProjectState = prevProjectUtils.getState();

            if (prevProjectState != prevProjectUtils.states.Draft) {
                gs.addErrorMessage(gs.getMessage("Requested item added to a project not in Draft state, cannot be removed."));
                current.setAbortAction(true);
            } else {
                validateTask(current);
                validateCurrentProject(current);
            }
        } else {
            validateTask(current);
            validateCurrentProject(current);
        }
    }

    function validateCurrentProject(current) {
        var currentProjectId = current.getValue("localization_project");
        var currentProjectUtils = new LFProjectUtils(currentProjectId);
        if (currentProjectUtils.hasValidRecord()) {
            var currentProjectState = currentProjectUtils.getState();

            if (currentProjectState != currentProjectUtils.states.Draft) {
                gs.addErrorMessage(gs.getMessage("Requested item cannot be added to a project not in Draft state."));
                current.setAbortAction(true);
            }
        } else {
            if (currentProjectId && currentProjectId != "") {
                gs.addErrorMessage(gs.getMessage("Requested item cannot be added to invalid project record."));
                current.setAbortAction(true);
            }
        }
    }

    function validateTask(current) {
        var taskId = current.getValue("localization_task");
        var taskUtils = new LFTaskUtils(taskId);
        if (taskUtils.hasValidRecord()) {
            gs.addErrorMessage(gs.getMessage("Requested item already assigned to a task cannot be added to a project."));
            current.setAbortAction(true);
        }
    }

})(current, previous);
