x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 if((!(new global.CSBusinessRuleUtilsImpl().isExcluded('d26abb053b930300b5c42479b3efc4c0', current.sys_class_name))))
  return;

 var sync_fields = gs.getProperty('sn_customerservice.parent_child_ case_sync', false);
 var hasChangedFields = false;

 if (sync_fields == "true") {

  var syncEnabled = false;

  //Additional check to see if the record is eligible for syncing
  var syncHelper = new CSCaseSyncHelper();
  syncEnabled = syncHelper.isCaseSyncEnabled(current);

  if (syncEnabled) {

   //get the comma seperated fields to be synchcronized
   var fieldMap = {};
   var field_list = gs.getProperty('sn_customerservice.case_fields_to_sync','').split(',');
   for (var i=0; i<field_list.length; i++) {
    var field_name=field_list[i].trim();
    if (current[field_name].changes()) {
     hasChangedFields=true;
     var f_name = current.getElement(field_name);
     var elemDesc = f_name.getED().getInternalType();
     if (elemDesc == 'journal_input') {
      fieldMap[field_name] = current[field_name].getJournalEntry(1);
     } else {
      fieldMap[field_name] = current.getValue(field_name);
     }
    }
   }
   //check if at least one field is modified and perform an async operation to update first level children
   if (hasChangedFields) {
    var canEmit=false;
    if (gs.nil(current.parent)) {
     var gr = new GlideRecord("sn_customerservice_case");
     gr.addQuery("parent", current.sys_id);
     gr.query();
     if (gr.hasNext()) {
      canEmit = true;
     }
    } else {
     var parentGr = new GlideRecord("sn_customerservice_case");
     parentGr.addQuery("sys_id", current.parent);
     parentGr.query();
     if (parentGr.next()) {
      if (parentGr.getDisplayValue("sync_driver") == 'false') {
       gs.info("Set sync_driver");
       canEmit = true;
      }
     }
    }
    if (canEmit) {
     current.setValue("sync_driver", true);
     gs.eventQueue('case_parent_child_sync', current, JSON.stringify(fieldMap));
    }
   }
  }
 }
})(current, previous);
