function onBefore(current, previous) {

  if (current.isActionAborted())
   return;

  // validate that encryption type is supported for attachments
  if (!(current.encryption_type.toString().endsWith('dynamic'))) {
   gs.addErrorMessage(gs.getMessage('Only standard encryption is supported for attachments'));
   current.setAbortAction(true);
   return;
  }

  if (current.active == true) {
   // abort if attachments already encrypted for current table or any ancestor
   // by encryption configuration Other than current one
   var gr = new GlideRecord('sys_encryption_configuration');
   gr.addQuery('table', 'IN', (new GlideTableHierarchy(current.table)).getHierarchy());
   gr.addQuery('type', 'Attachment');
   gr.addQuery('active', true);
   gr.addQuery('sys_id', '!=', current.sys_id);
   gr.query();
   if (gr.next()) {
    gs.addErrorMessage(gs.getMessage('Attachments are already encrypted for table {0} and its extensions', gr.table));
    current.setAbortAction(true);
    return;
   }
  }

  // column field is irrelevant for attachments encryption configuration
  current.column = '';
 }
