x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 var previousFactsTable = SNC.SpotlightUtil.getFactsTable(previous.indicator);
 var currentFactsTable = SNC.SpotlightUtil.getFactsTable(current.indicator);

 // if the current and previous facts table are different do the following:
 // 1. Create a new DB view if a suitable one does not exist or chose an existing one to relate to this record
 // 2. Remove the DB view for this spotlight group if it is not being used by any other spotlight group
 if (currentFactsTable !== previousFactsTable) {
  var dbView = SNC.SpotlightUtil.createDbView(currentFactsTable);
  if (dbView === null) {
   gs.addErrorMessage(gs.getMessage("Unable to create database view"));
   current.setAbortAction(true);
   return;
  }
  //Will remove the old DB view only after the new DB view is been created to avoid empty relation on spotlight group if DB view creation fails
  if (!SNC.SpotlightUtil.removeDBView(previousFactsTable, current.sys_id)) {
   gs.addErrorMessage(gs.getMessage("Unable to remove database view with sys_id {0} for spotlight group '{1}'", [previous.database_view, current.name]));
  }

  current.setValue('database_view', dbView);
 }
})(current, previous);
