(function executeRule(current, previous /*null when async*/) {
 if (current.document.hr_task_type == 'e_signature') {
  var documentDetails = new sn_esign.esign_taskUtils().getManagedDocumentDetails(current.document.sn_esign_document);
  var revisionID = documentDetails.table_sys_id;
  var doc = new GlideRecord('sn_hr_core_document_acknowledgement');
  doc.initialize();
  doc.setValue('acknowledgement_type', 'e_signature');
  doc.setValue('signature', current.getUniqueValue());
  doc.setValue('acknowledged','true');
  doc.setValue('table_name', 'sn_hr_core_task');
  doc.setValue('table_sys_id', current.document);
  doc.setValue('document_revision', revisionID);
  doc.setValue('user', gs.getUserID());
  doc.update();

 } else if (current.document.hr_task_type == 'sign_document') {
  var signAck = new GlideRecord('sn_hr_core_document_acknowledgement');
  signAck.initialize();
  signAck.setValue('acknowledgement_type', 'sign_document');
  signAck.setValue('signature', current.getUniqueValue());
  signAck.setValue('acknowledged','true');
  signAck.setValue('table_name', 'sn_hr_core_task');
  signAck.setValue('table_sys_id', current.document);
  signAck.setValue('user', gs.getUserID());
  signAck.update();
 }
})(current, previous);
