(function executeRule(current, previous /*null when async*/ ) {

    current.work_notes = 'Initial Assessment has been added to the current record';
    var i;
    var asmt = ['489d5c9a47243110020ba9f8536d43b8', '32d0083a47743510020ba9f8536d43a7', 'b02f65fa47bc3510020ba9f8536d4340'];

    for (i = 0; i < asmt.length; i++) {
        //Creation of assessment instance record
        var typeSysID = asmt[i]; //The sys_id of the assessment, should be in published state
        var sourceRecordID = current.sys_id; //sys_id of the transaction record (Ex: sys_id of the IMS record)
        var userID = current.submitter.sys_id; // sys_id in the opened_by field
        var result = new SNC.AssessmentCreation().createAssessments(typeSysID, sourceRecordID, userID); // successfully created the Survey instance record
        //gs.addInfoMessage('result: '+ result );

        //Association of Trigger ID and Trigger Records
        var asmtGR = new GlideRecord("asmt_assessment_instance");
        asmtGR.get(result.split(",")[0]); // gets the sys_id of the newly created assessment record from the 'result' variable array
        asmtGR.setValue('trigger_table', 'dmn_demand'); //setting target table to demand
        asmtGR.setValue('task_id', current.sys_id); //sys_id of the task id to the current record
        asmtGR.setValue('trigger_id', current.sys_id);
        asmtGR.update();
    }

    var dmnTask = new GlideRecord('dmn_demand_task');
    dmnTask.initialize();
    dmnTask.parent = current.sys_id;
    dmnTask.short_description = 'LRCC Task';
    dmnTask.insert();

})(current, previous);
