(function executeRule(current, previous /*null when async*/) {

 var logUtil = new AWALoggingUtils();
 if (!current.document_id || current.document_table != 'interaction'){
  logUtil.atDebug(current).log("WorkItem - Accepted: document_id field missing or document_table is not interaction");
  return;
 }

 var interaction = new GlideRecord('interaction');
 if (!interaction.get(current.document_id)){
  logUtil.atWarn(current).log("WorkItem - Accepted: could not find interaction record {0}", current.document_id);
  return;
 }

 if (!interaction.channel_metadata_document || !interaction.channel_metadata_table){
  logUtil.atWarn(current).log("WorkItem - Accepted: channel_metadata_document field is empty for interaction");
  return;
 }

 // current usecase only applies to interactions that are conversation based.
 if(interaction.channel_metadata_table != 'sys_cs_conversation' || !(new InteractionTypeUtil()).canSendMessages(interaction)) {
  logUtil.atDebug(current).log("WorkItem - Accepted: interaction cannot send messages");
  return;
 }

 logUtil.atDebug(current).log("WorkItem - Accepted - Success: {0} accepted by {1}",
  interaction.number, current.assigned_to.getDisplayValue());
 new SNC.ConnectListeners().queueEntryAccepted(interaction.getValue('channel_metadata_document'));

})(current, previous);
