x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous) {
    try {
        var isDataSeparationSkippedFromNowOnwards = typeof DataSeparatorGlobalUtil !== "undefined" ? DataSeparatorGlobalUtil.skipDataSeparation() : false;
        var isCostPlanBatch = (typeof costPlanBatch !== 'undefined') && JSUtil.notNil(costPlanBatch);
        if (isCostPlanBatch && costPlanBatch)
            return;

        var util;
        if (JSUtil.notNil(current.getValue('task')) && JSUtil.notNil(current.getValue('expense_type'))) {
            util = new PPMCostRollupManager(current.getValue('fiscal_period'), current.getValue('expense_type'));

            // Can we utilize the same from parent cost plan
            var task = current.task.getRefRecord();
            if (task.instanceOf('pm_project') || task.instanceOf('pm_project_task')) {
                util.updateProjectEstimatedCostByFiscalPeriod(task.getValue('top_task'));
            } else if (task.instanceOf('dmn_demand')) {
                util.updateDemandEstimatedCostByFiscalPeriod(current.getValue('task'));
            }
        }

        if ((current.task.changes() || current.expense_type.changes()) && JSUtil.notNil(previous.getValue('task')) && JSUtil.notNil(previous.getValue('expense_type'))) {
            util = new PPMCostRollupManager(previous.getValue('fiscal_period'), previous.getValue('expense_type'));
            var previousTask = previous.task.getRefRecord();
            if (previousTask.instanceOf('pm_project') || previousTask.instanceOf('pm_project_task')) {
                util.updateProjectEstimatedCostByFiscalPeriod(previousTask.getValue('top_task'));
            } else if (previousTask.instanceOf('dmn_demand')) {
                util.updateDemandEstimatedCostByFiscalPeriod(previous.getValue('task'));
            }
        }
    } finally {
        if (isDataSeparationSkippedFromNowOnwards)
            DataSeparatorGlobalUtil.honourDataSeparation();
    }

})(current, previous);
