x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
    var woCompletionflag = true;
    var stateFlag;
    var gr = new GlideRecord("wm_task");
    gr.addQuery('parent', current.parent);
    gr.query();
    while (gr.next()) {
        if (gr.getValue('state') != '3' && gr.getValue('state') != '4' &&
            gr.getValue('state') != '7' && gr.getValue('number') != current
            .number) {
            woCompletionflag = false;
            break;
        }
    }
    if (current.state && current.state == '7' /*cancelled*/ ) {
        stateFlag = 'cancelled';
    } else {
        stateFlag = 'closed';
    }
    if (gs.isMobile() && GlidePluginManager.isActive('com.snc.fsm_task_bundle') && (current.is_bundle == true))
 return;
    if (woCompletionflag){
        var device = GlideMobileExtensions.getDeviceType();
        if (device != "doctype") {
         gs.addInfoMessage(gs.getMessage(
             'Task {2} {0} successfully. {1} is also closed', [stateFlag,
                '<a href="#/form/wm_order/' +
                current.parent.sys_id + '">Work Order (' + current.parent
                .number + ')</a>', current.number
         ]));
  } else {
         gs.addInfoMessage(gs.getMessage(
          'Task {2} {0} successfully. {1} is also closed', [stateFlag,
          'Work Order (' + current.parent
          .number + ')', current.number
            ]));
        }
    } else
        gs.addInfoMessage(gs.getMessage('Task {0} {1} successfully', [current.number, stateFlag]));

})(current, previous);
