x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    var metType = current.metric_type.getDisplayValue(); //Metric name
    //Original Copy
    if (metType == '') {
        var parRec = current.task_id.getValue('sys_id'); //Demand Record
        var asmtRec = current.getValue('sys_id'); //Assessment Record
        //var metType = current.metric_type.getDisplayValue(); //Metric name
        var subBy = current.user.getDisplayValue(); //Assigned to

        var parent_record = new GlideRecord('dmn_demand');
        if (parent_record.get(parRec)) {
            var workNotes = '';
            var survey_response = new GlideRecord('asmt_metric_result');
            survey_response.addQuery('instance', asmtRec);
            survey_response.query();
            while (survey_response.next()) {

                workNotes += '<style> table, th, td {border: 1px solid black;border-collapse: collapse;background-color:lightyellow;padding:2px;text-align: left;}</style><table style="width:100%">';

                // Assuming assessment responses are stored in the "Question" field
                workNotes += '<tr style="height: 50.4px;"><td style="width: 500px; height: 15.4px;"><strong>' + survey_response.instance_question.getDisplayValue() + '</strong></td>' + '<td style="text-align: center; "width: 700px; height: 15.4px;"">' + survey_response.string_value + '</td>';

                // End the table
                workNotes += '</table>';

            }

            parent_record.work_notes = '[code]' + '<p style="text-align: center;"><span style="font-size: 14pt;"><strong>' + metType + " " + '</strong>(<em><span style="font-size: 10pt;">Submitted by: <strong>' + subBy + '</strong></span></em>)</span></p>' + workNotes + '[/code]';
            parent_record.update();
        }
    }



})(current, previous);
