// Removes any CIs which do not have a manual change assigned and not the CI associated to the change
(function executeRule(current, previous) {
 var taskci = new GlideRecord('task_ci');
 taskci.addQuery('task',current.getUniqueValue());
 taskci.addNotNullQuery("ci_item");
 if (JSUtil.notNil(current.cmdb_ci))
  taskci.addQuery("ci_item","!=",current.cmdb_ci);
 taskci.addQuery("manual_proposed_change","!=",true);
 taskci.query();

 var showDeleteMessage = false;
 /* If it's not a bulk update any more, remove any CIs which are not
  * manual proposed changes.  Otherwise spin through the affected CI
  * list and delete any which the mass change no longer applied to
  */
 if (current.is_bulk == false)
  taskci.deleteMultiple();
 else
  while (taskci.next()) {
   // If the new class is the same as the CI's class no need to delete
   if (taskci.ci_item.sys_class_name+"" == current.ci_class+"")
    continue; //No need to delete

   //If the new class is a prent of the CI class no need to delete
   var tu = new TableUtils(current.ci_class);
   if (tu.getTableExtensions().contains(taskci.ci_item.sys_class_name))
    continue;

   // If the proposed mass change no longer applies, delete the record.
   taskci.deleteRecord();
   showDeleteMessage=true;
  }

 if (showDeleteMessage)
  gs.addInfoMessage(gs.getMessage("Affected CIs have been modified to reflect new CI class for mass change"));
})(current, previous);
