// delete all the skills the user had granted only from this group

//Find the other groups in which user is present
var userGroupRecord = new GlideRecord('sys_user_grmember');
userGroupRecord.addQuery('user', current.user);
userGroupRecord.addQuery('group', '!=', current.group);
userGroupRecord.query();

//Find the skills user inherited from other groups
var skillsNotInGroupMap = {};
while (userGroupRecord.next()) {
    var groupSkillRecord = new GlideRecord('sys_group_has_skill');
    groupSkillRecord.addQuery('group', userGroupRecord.group);
    groupSkillRecord.query();
    while (groupSkillRecord.next()) {
        if (groupSkillRecord.skill != undefined) {
            skillsNotInGroupMap[groupSkillRecord.skill + ""] = groupSkillRecord.group;
        }
    }
}

// Find the skills user inherited from the current group
var skillInGroupRecord = new GlideRecord('sys_group_has_skill');
var skillsInGroupList = [];
skillInGroupRecord.addQuery('group', current.group);
skillInGroupRecord.query();
while (skillInGroupRecord.next()) {
    skillsInGroupList.push(skillInGroupRecord.skill + "");
}
var cmnSkillsGrpMap = {};
var skillsOnlyInGroup = [];
//Find the skills which are in current group but not in other groups
for (var i = 0; i < skillsInGroupList.length; i++) {
    if (skillsInGroupList[i] in skillsNotInGroupMap) {
        cmnSkillsGrpMap[skillsInGroupList[i]] = skillsNotInGroupMap[skillsInGroupList[i]];
    } else {
        skillsOnlyInGroup.push(skillsInGroupList[i]);
    }
}


//Delete the row in User Skill table for the current user with the skills only in current group
var groupSkillsRecord = new GlideRecord('sys_user_has_skill');
groupSkillsRecord.addQuery('user', current.user);
groupSkillsRecord.addQuery('skill', 'IN', skillsOnlyInGroup);
groupSkillsRecord.addQuery('inherited', true);
groupSkillsRecord.deleteMultiple();

var cmnSkillGrpRec;
var skill;
for (skill in cmnSkillsGrpMap) {
    cmnSkillGrpRec = new GlideRecord('sys_user_has_skill');
    cmnSkillGrpRec.addQuery('user', current.user);
    cmnSkillGrpRec.addQuery('skill', skill);
    cmnSkillGrpRec.addQuery('inherited', true);
    cmnSkillGrpRec.setValue('inherited_from', cmnSkillsGrpMap[skill]);
    cmnSkillGrpRec.updateMultiple();
}
