x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
    var programIds = [current.getValue('sys_id')];
    var portfolioId = current.getValue('portfolio');
    g_scratchpad.hasProjectAndDemand = false;
    g_scratchpad.originalPortfolioId = portfolioId;

    var projects = new GlideAggregate('pm_project');
    projects.addQuery('primary_program', 'IN', programIds);
    projects.addAggregate('COUNT');
    projects.query();

    var demands = new GlideAggregate('dmn_demand');
    demands.addQuery('primary_program', 'IN', programIds);
    demands.addAggregate('COUNT');
    demands.query();

    if (projects.next()) {
        if (projects.getAggregate('COUNT') > 0)
            g_scratchpad.hasProjectAndDemand = true;
    }
    if (demands.next()) {
        if (demands.getAggregate('COUNT') > 0)
            g_scratchpad.hasProjectAndDemand = true;
    }

})(current, previous);
