x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    // 1. Find parent categories
    var parentCategories = [];
    var pFound = false;
    var cFound = false;
    if (!gs.nil(current.parent)) {
        parentCategories = current.parent.cmdb_model_category;
    }

    // 2. Find children categories
    var childCategories = sn_ent.EnterpriseContentEAMUtils.getClassificationChildenCategories(current);

    var validCategories = [];
    var invalidCategories = [];

    // 3. Validate current categories
    var currentCategories = new GlideRecord("cmdb_model_category");
    currentCategories.addEncodedQuery("sys_idIN" + current.cmdb_model_category);
    currentCategories.query();
    while (currentCategories.next()) {
        pFound = false;
        cFound = false;

        // Validate with parent
        if (parentCategories.length == 0) {
            pFound = true;
        } else {
            var parent = currentCategories.getValue("sys_id");
            while (parent != undefined) {
                if (parentCategories.indexOf(parent) >= 0) {
                    pFound = true;
                    break;
                }
                var rec = new GlideRecord('cmdb_model_category');
                rec.get(parent);
                parent = rec.getValue("parent_cateogry");
            }
        }

        // Validate with children
        if (childCategories.length == 0) {
            cFound = true;
        } else {
            for (var i = 0; i < childCategories.length; i++) {
                var child = childCategories[i];
                while (child != undefined) {
                    if (child + '' === currentCategories.getValue('sys_id') + '') {
                        cFound = true;
                        break;
                    }
                    rec = new GlideRecord('cmdb_model_category');
                    rec.get(child);
                    child = rec.getValue("parent_cateogry");
                }
                if (cFound) break;
            }
        }

        if (pFound && cFound) {
            validCategories.push(currentCategories.getValue('sys_id'));
        } else {
            invalidCategories.push(currentCategories.getValue('name'));
        }
    }

    if (invalidCategories.length > 0)
        gs.addErrorMessage(gs.getMessage("Cannot assign following categories {0}.", [invalidCategories.toString()]));
    current.setValue('cmdb_model_category', validCategories);
})(current, previous);
