x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 var actionHelper = new RMUIActionHelper(current);
 if(current.operation() == 'update' && !actionHelper.canChangeTask(previous)) {
  current.setAbortAction(true);
  gs.addErrorMessage(gs.getMessage('The task cannot be changed because the resource plan has actuals or it is in completed or canceled state.'));
 } else {
  var task = current.task.getRefRecord();
  var isPlannedTask = task.instanceOf('planned_task');
  var isDemand = task.instanceOf('dmn_demand');
  var taskDomain = task.getValue('sys_domain');
  var topTask = '';
  var program = '';
  var portfolio = '';
  if(isPlannedTask) {
   if(JSUtil.notNil(task.getValue('top_task')))
    topTask = task.getValue('top_task');
   if(JSUtil.notNil(task.getValue('top_program')))
    program = task.getValue('top_program');
   if(JSUtil.notNil(task.getValue('top_portfolio')))
    portfolio = task.getValue('top_portfolio');
  }
  else if (isDemand) {
   if(JSUtil.notNil(task.getValue('primary_program')))
    program = task.getValue('primary_program');
   if(JSUtil.notNil(task.getValue('portfolio')))
    portfolio = task.getValue('portfolio');
   topTask = '';
  }
  else {
   program = '';
   portfolio = '';
   topTask = '';
  }
  current.setValue('top_task',topTask);
  current.setValue('program',program);
  current.setValue('portfolio',portfolio);
  if(current.getValue('sys_domain') != taskDomain)
   current.setValue('sys_domain', taskDomain);
 }

})(current, previous);
