(function executeRule(current, previous /*null when async*/ ) {

    //Parameters passed to Script Include
    var dmnUtil = new wfDemandUtils(); // Script Include
    var shortDesc; //Parameters for Assessmnet on Demand Task
    var metType; //Parameters for Assessmnet on Demand Task
    var dmnTask; //Parameters for Assessmnet on Demand Task
    var assignTo; //Parameters for Assessmnet on Demand Task
    var dmnRec; //Parameters for Assessmnet on Demand
    var trigID; //Paremeter for Parent Record

    if ((current.correlation_display == 'Review Meeting') && (current.operation() == 'insert')) { //Creation of  Functional Change Committee Review Assessment on creation of Review Meeting
        metType = gs.getProperty('wf.demand.func.comm.review');
        dmnTask = current.getValue('sys_id');
        assignTo = current.parent.opened_by.getValue('sys_id');
        dmnUtil.createDemandTaskAssessment(metType, dmnTask, assignTo);
    }
    //Creation of  Functional Change Committee Review Assessment on creation of Review Meeting
    else if ((current.correlation_display == 'Review Meeting') && (current.state == 3) && current.state.changes()) { //Promoting Demand to Qualified State and triggering Initiative Refinement Questionnaire

        var dmnParent = new GlideRecord('dmn_demand'); //Promote Demand to Qualified Once the review meeting Task is Closed
        var parRecord = current.parent.getValue('sys_id');
        dmnParent.addQuery('sys_id', parRecord);
        dmnParent.query();
        while (dmnParent.next()) {
            dmnParent.state = '-4';
            dmnParent.update();
        } //Promote Demand to Qualified Once the review meeting Task is Closed

        //Creation of Initiative Refinement Questionnaire
        metType = gs.getProperty('wf.init.refine.questionnaire');
        dmnRec = current.parent.getValue('sys_id');
        assignTo = current.parent.opened_by.getValue('sys_id');
        dmnUtil.createDemandAssessment(metType, dmnRec, assignTo);

    } //Creation of Initiative Refinement Questionnaire
    else if ((current.correlation_display == 'Final CFIT Result Review') && (current.state == 3) && current.state.changes()) { //Creation of Success Criteria Assessment
        metType = gs.getProperty('wf.success.crit.key.objectives');
        dmnRec = current.parent.getValue('sys_id');
        assignTo = current.parent.opened_by.getValue('sys_id');
        dmnUtil.createDemandAssessment(metType, dmnRec, assignTo);

    } //Creation of Success Criteria Assessment

    //Task Creation for Prepod Validation
    else if ((current.correlation_display == 'Prototype Delivery') && (current.state == 3) && current.state.changes()) { //Demand Task Creation for Prepod Validation when Prototype Delivery is Completed
        trigID = current.parent.getValue('sys_id');
        shortDesc = "Preprod Validation";
        dmnUtil.createDemandTask(trigID, shortDesc);
    }
    //Task Creation for Prepod Validation

    //Creation of assessment instance record for the Prepod Validation demand task
    else if ((current.correlation_display == 'Preprod Validation') && (current.operation() == 'insert')) { //Assessment for Prepod Validatiopn once the Task is Created from above
        metType = gs.getProperty('wf.preprod.validation');
        dmnTask = current.getValue('sys_id');
        assignTo = current.parent.opened_by.getValue('sys_id');
        dmnUtil.createDemandTaskAssessment(metType, dmnTask, assignTo);

    } //Creation of assessment instance record for the Prepod Validation demand task
    else if ((current.correlation_display == 'Review Ideation Assessment Result') && (current.operation() == 'insert')) {
        metType = gs.getProperty('wf.init.review.result.questionnaire');
        dmnTask = current.getValue('sys_id');
        assignTo = current.parent.opened_by.getValue('sys_id');
        dmnUtil.createDemandTaskAssessment(metType, dmnTask, assignTo);

    } else if ((current.correlation_display == 'Review Ideation Assessment Result') && (current.state == 3) && current.state.changes()) { //Creation CFIT Results Assessment
        metType = gs.getProperty('wf.demand.cfit.result.asessment');
        dmnRec = current.parent.getValue('sys_id');
        assignTo = current.parent.opened_by.getValue('sys_id');
        dmnUtil.createDemandAssessment(metType, dmnRec, assignTo);

    }
    //Creation CFIT Results Assessment
    else if ((current.correlation_display == 'Initial CFIT Result Review') && (current.state == 3) && current.state.changes()) { //Creation of Final CFIT Result Review Task
        trigID = current.parent.getValue('sys_id');
        shortDesc = "Final CFIT Result Review";
        dmnUtil.createDemandTask(trigID, shortDesc);

    }
    //Creation of Final CFIT Result Review Task
    else if ((current.correlation_display == 'Final CFIT Result Review') && (current.operation() == 'insert')) { //Creation of Final CFIT Assessment
        metType = gs.getProperty('wf.final.cfit.result.review');
        dmnTask = current.getValue('sys_id');
        assignTo = current.parent.opened_by.getValue('sys_id');
        dmnUtil.createDemandTaskAssessment(metType, dmnTask, assignTo);

    } //Creation of Final CFIT Assessment




})(current, previous);
