function findGuestUserUc() {

    var guestUserUC = new GlideRecord('user_criteria');
    guestUserUC.addEncodedQuery("name=Guest User^ORsys_id=76f09af6cb1200108ad442fcf7076dbf");
    guestUserUC.query();
    if (guestUserUC.next())
        return guestUserUC.sys_id;
    else
        return createGuestUserUC();

}

function createGuestUserUC() {

    var userCriteria = new GlideRecord("user_criteria");
    userCriteria.initialize();
    userCriteria.setValue('name','Guest User');
    userCriteria.setValue('advanced',true);
    userCriteria.setValue('script','answer = user_id == GlideUser.getUser("guest").getID();');
    userCriteria.setValue('user','5136503cc611227c0183e96598c4f706');
    var userCriteria1 = new GlideRecord('user_criteria');
    userCriteria1.get(userCriteria.insert());
    userCriteria1.sys_domain = 'global';
    return userCriteria1.update();

}
