getTaskEarliestSDate();

function getTaskEarliestSDate() {

    var taskSysId = (current.isNewRecord()) ? current.parent.sys_id : current.sys_id;
    g_scratchpad.projectTaskSysId = taskSysId;
    var gr = new GlideRecord('planned_task');
    gr.addQuery("sys_id", taskSysId);
    gr.query();
    if (gr.next()) {
        // We will utilize the PlannedTaskManager script include to perform the calculations.
        // var ptm = new SNC.ProjectTaskManager();
        // ptm.loadAncestors(gr);
        // g_scratchpad.pTaskStartDate = ptm.getTaskStartDate(taskSysId);
        var plannedTaskAPI = new SNC.PlannedTaskAPI();
        g_scratchpad.pTaskStartDate = plannedTaskAPI.getTaskStartDate(taskSysId);
    }
    var project = current.parent.top_task;
    var tDate = new GlideDateTime();
    if (project.work_start.nil()) {
        tDate.setValue(current.parent.top_task.start_date);
        g_scratchpad.projectStartDate = tDate.getDisplayValue();
    } else {
        tDate.setValue(current.parent.top_task.work_start);
        g_scratchpad.projectStartDate = tDate.getDisplayValue();
    }
    g_scratchpad.nestedPrjConstraintDate = !gs.nil(current.parent.constraint_date) ? current.parent.constraint_date.getDisplayValue() : current.getDisplayValue('start_date');
    var parentDuration = new GlideDuration();
    parentDuration.setValue(current.parent.duration);
    g_scratchpad.nestedPrjInitialDuration = parentDuration.getDurationValue();
    if (current.parent.approved_start_date) {
        var pApprovedStartDate = new GlideDateTime();
        pApprovedStartDate.setValue(current.parent.approved_start_date);
        g_scratchpad.nestedPrjApproveStartDate = pApprovedStartDate.getDisplayValue();
    }
    if (current.parent.approved_end_date) {
        var pApprovedEndDate = new GlideDateTime();
        pApprovedEndDate.setValue(current.parent.approved_end_date);
        g_scratchpad.nestedPrjApproveEndDate = pApprovedEndDate.getDisplayValue();
    }

}
