x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
    //Creation of assessment instance record
    var dmnTaskGr1 = new GlideRecord('dmn_demand_task');
    dmnTaskGr1.initialize();
    dmnTaskGr1.parent = current.trigger_id.getValue('sys_id');
    dmnTaskGr1.short_description = "Review Meeting";
    dmnTaskGr1.assigned_to = current.parent.opened_by.getValue('sys_id');
    dmnTaskGr1.description = "1. Please Review Assessment in the work notes" + "\n" + "2. Once Completed Mark this Task as Done";
    dmnTaskGr1.insert();

    //Creation of assessment instance record
    var typeSysID = 'b02f65fa47bc3510020ba9f8536d4340'; //The sys_id of the assessment, should be in published state
    var sourceRecordID = dmnTaskGr1.getValue('sys_id'); //sys_id of the transaction record (Ex: sys_id of the IMS record)
    var userID = current.parent.assigned_to.getValue('sys_id'); // sys_id in the opened_by field
    var result = new SNC.AssessmentCreation().createAssessments(typeSysID, sourceRecordID, userID); // successfully created the Survey instance record
    //gs.addInfoMessage('result: '+ result );

    //Association of Trigger ID and Trigger Records
    var asmtGR = new GlideRecord("asmt_assessment_instance");
    asmtGR.get(result.split(",")[0]); // gets the sys_id of the newly created assessment record from the 'result' variable array
    asmtGR.setValue('trigger_table', 'dmn_demand_task'); //setting target table to demand
    asmtGR.setValue('trigger_id', dmnTaskGr1.getValue('sys_id'));
    asmtGR.setValue('task_id', dmnTaskGr1.getValue('sys_id')); //sys_id of the task id to the current demand record
    asmtGR.update();

})(current, previous);
