(function executeRule(current, previous /*null when async*/ ) {

    var MIN_DURATION = 900000; //15 minutes in milliseconds
    var startDate = new GlideDateTime(current.start_date);
    var endDate = new GlideDateTime(current.end_date);
    var currentDate = new GlideDateTime();

    if (current.state == 3) {
        if (!current.start_date) {
            gs.addErrorMessage(gs.getMessage("Invalid Start Date"));
            current.setAbortAction(true);
        }

        if (!current.end_date) {
            gs.addErrorMessage(gs.getMessage("Invalid End Date"));
            current.setAbortAction(true);
        }
        if (!current.meeting_details) {
            gs.addErrorMessage(gs.getMessage("Invalid meeting details"));
            current.setAbortAction(true);
        }
    }

    if (current.start_date && current.end_date) {
        if (current.end_date <= current.start_date) {
            gs.addErrorMessage(gs.getMessage("End Date {0} must be after Start Date {1}", [current.end_date.getDisplayValue(), current.start_date.getDisplayValue()]));
            current.setAbortAction(true);
        } else if ((endDate.getNumericValue() - startDate.getNumericValue()) < MIN_DURATION) {
            gs.addErrorMessage(gs.getMessage("The duration of the meeting needs to be a minimum of 15 minutes."));
            current.setAbortAction(true);
        } else if (startDate.getNumericValue() < currentDate.getNumericValue() || endDate.getNumericValue() < currentDate.getNumericValue()) {
            var meetingFields = ["start_date", "end_date", "attendees", "external_attendees"];
            for (var field in current) {
                if (meetingFields.includes(field) && current[field] != previous[field]) {
                    gs.addErrorMessage(gs.getMessage("Start Date or End Date cannot be in past"));
                    current.setAbortAction(true);
     break;
                }
            }
        }
    }

})(current, previous);
