x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 if (!new sn_edge_encryption_ns.EdgeVersionCompatibilityValidator().isFeatureSupported("Service-Catalog-Variables")) {
  gs.addErrorMessage(gs.getMessage("One or more Edge Proxy servers are Pre-Tokyo. Please upgrade all Edge Proxy servers to Tokyo or later version for Service Catalog Variable support"));
  current.setAbortAction(true);
  return;
 }

 // variable can't be empty
 if (current.variable.nil()) {
  gs.addErrorMessage(gs.getMessage('Choose a variable to encrypt'));
  current.setAbortAction(true);
  return;
 }

 // variable must exist
 var variable = new GlideRecord('item_option_new');
 variable.addQuery('sys_id', current.variable);
 variable.addQuery('type', 'IN', '9,10,26,23,28,2,6,27,16');
 variable.query();
 if (!variable.next()) {
  gs.addErrorMessage(gs.getMessage('The specified variable does not exist or is not a supported type'));
  current.setAbortAction(true);
  return;
 }

 var config = new GlideRecord('sys_encryption_variable_configuration');
 config.addQuery('variable', current.variable);
 config.addQuery('sys_id', '!=', current.sys_id);
 config.query();
 if (config.next()) {
  gs.addErrorMessage(gs.getMessage('The selected variable already has an encryption configuration'));
  current.setAbortAction(true);
  return;
 }

})(current, previous);
