Service-now.com

Description:
Automatically activates with plugin Time Recording
 **/

  var resp = processTimeCard();
  if (resp && !resp.success && JSUtil.notNil(resp.msg))
    gs.addErrorMessage(resp.msg);
  if (resp && resp.success && JSUtil.notNil(resp.msg))
    gs.addInfoMessage(resp.msg);


function processTimeCard() {
    var resp = {};
 var timeRecordingHelper = new global.TimeRecordingHelper();
 if (current.operation() == "insert" || current.operation() == "delete") {
  // create new or modify current time card
  if (JSUtil.notNil(current) && JSUtil.notNil(current.work_date))
        resp = timeRecordingHelper.processTimeCard(current);
      return resp;
 } else if (current.operation() == "update") {
  // modify previous and current time cards
  if (JSUtil.notNil(previous) && (current.category.changes() || current.work_date.changes() || current.rate_type.changes()) && JSUtil.notNil(current.work_date)) {
        resp = timeRecordingHelper.processTimeCard(previous);
        if (resp && !resp.success)
          return resp;
  }
  if (JSUtil.notNil(current) && JSUtil.notNil(current.work_date)) {
        resp = timeRecordingHelper.processTimeCard(current);
        return resp;
  }
 }
}
