(function executeRule(current, previous /*undefined when async or display*/ ) {
 // Add your code here
 var user = getUserFromConsumer(current.consumer);
 var roleName = 'sn_acct_consumer.consumer';
 if(user) {
  var role = new GlideRecord('sys_user_role');
  role.addQuery('name', roleName);
  role.query();
  if (role.next()) {
   roleId = role.getValue('sys_id');
   if (!userHasRole(user.sys_id,roleId)) {
    var uhr = new GlideRecord("sys_user_has_role");
    uhr.initialize();
    uhr.user = user.sys_id;
    uhr.role.setDisplayValue(roleName);
    gs.addInfoMessage(gs.getMessage("Adding Role {0} to {1}",[roleName,user.user_name]));
    uhr.insert();
   }
  }
 }

})(current, previous);

function userHasRole(user, roleId) {
 var num = 0;
 var uhr = new GlideAggregate("sys_user_has_role");
 uhr.addAggregate("COUNT");
 uhr.addQuery("user", user);
 if (roleId)
  uhr.addQuery("role", roleId);
 uhr.query();
 if (uhr.next())
  num = uhr.getAggregate("COUNT");
 if (num > 0) {
  return true;
 }
 return false;
}

function getUserFromConsumer(consumer){
 if(consumer && consumer.user){
  return consumer.user;
 }

}
