x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 var util = new FSMDocumentTemplateUtils();
 var sigImageGR = new GlideRecord('signature_image');
 var cur_table_sys_id = current.table_sys_id;
 sigImageGR.addQuery('sys_id', cur_table_sys_id);
 sigImageGR.query();

 if (sigImageGR.next()) {
  var tableName = sigImageGR.table;
  var docTaskId = sigImageGR.document;

  //If document task generated from a Work Order, update the signature image and execute the flow
  if(tableName == 'sn_doc_task' && util.hasWorkOrder(docTaskId)) {
   util.updateSignRecord(sigImageGR, docTaskId);
   tableName = 'wm_order';
  }
  if (tableName == 'wm_order') {

   var sm = new sn_sm.SMConfiguration(); 
   if (sm.isEnabled('field_service', 'pdf_order_summary', false)) {
    var signedName = sigImageGR.signed_name;
    var woSysId = sigImageGR.document;

    var wogr = new GlideRecord("wm_order");
    wogr.get(woSysId);
    var templateId = util.getTemplateId(wogr);
    var useDocTemplates = sm.isEnabled('field_service', 'use_document_template', false);
    if(useDocTemplates && !gs.nil(templateId)) {
     util.signAndComplete(wogr, sigImageGR);
    }
    else
     new GeneralWOForm(tableName, woSysId, signedName).generate();
    }
   }
  }  
    })(current, previous);
