x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 if(isTaskTypeChanged() || current.assigned_to.changesTo("")){
  current.survey_instance = "";
  current.update();
 }

 var gr = new GlideRecord("asmt_assessment_instance");
 if(gr.get(previous.survey_instance)){
  var refs = getSurveyRefCount(previous.survey_instance);
  if(gr.isValidRecord() && refs < 1){
   gr.setValue("state", "canceled");
   gr.update();
  }
 }

 function isTaskTypeChanged(){
  return (current.hr_task_type.changesTo("take_survey") && !current.survey.changes()) ||
      (current.hr_task_type.changesTo("collect_Information") && !current.employee_form.changes()) ||
      (current.hr_task_type.changesFrom("collect_Information") && !current.hr_task_type.changesTo("take_survey")) ||
      (current.hr_task_type.changesFrom("take_survey") && !current.hr_task_type.changesTo("collect_Information"));
 }


 function getSurveyRefCount(survey_instance){
  var gr = new GlideAggregate('sn_hr_core_task');
  gr.addAggregate("COUNT");
  gr.addQuery("survey_instance", survey_instance);
  gr.query();
  gr.next();
  return gr.getAggregate("COUNT");
 }
})(current, previous);
