x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 //Allow creation of upgrade schedule only if the selected proxy is out of date
 var proxyGr = new GlideRecord("sys_encryption_proxy");
 proxyGr.addQuery('guid', current.proxy_server);
 proxyGr.query();
 if (proxyGr.next()) {
  var proxyOutOfDate = sn_edge_encryption.EdgeEncryptionAutoUpgrade.isOutOfDate(proxyGr.proxy_build);
  if (!proxyOutOfDate) {
   gs.addInfoMessage(gs.getMessage("Selected Edge Encyption Proxy is up to date. Upgrade schedules can only be created on proxies that are out of date."));
   current.setAbortAction(true);
   return;
  } else if (proxyGr.proxy_build == 'unknown') {
   gs.addInfoMessage(gs.getMessage("Selected Edge Encyption Proxy is a pre-Jakarta version. Scheduled Upgrades are only available for Jakarta proxies or later. Please perform a manual upgrade for this proxy."));
   current.setAbortAction(true);
   return;
  }
 }

 var now = new GlideDateTime();
 //Give the user a window of 5 minutes to submit this record from the time he opens the create form.
 now.addSeconds(-300);
 if (now.compareTo(new GlideDateTime(current.scheduled_start_time)) > 0) {
  gs.addInfoMessage(gs.getMessage("The scheduled start time must be the current time or later"));
  current.setAbortAction(true);
  return;
 }

 // don't allow 2 active schedules for the same proxy
 var gr = new GlideRecord("sn_edge_encryption_proxy_upgrade_schedule");
 gr.addActiveQuery();
 gr.addQuery('proxy_server', current.proxy_server);
 gr.addQuery('status', 'IN', 'pending,running');
 gr.addQuery('sys_id', '!=', current.sys_id);
 gr.query();
 if (gr.next()) {
  gs.addInfoMessage(gs.getMessage("An active schedule already exists for this proxy"));
  current.setAbortAction(true);
  return;
 }


 if (gs.nil(current.target_version)) {
  gs.addInfoMessage(gs.getMessage("The target version must not be empty"));
  current.setAbortAction(true);
  return;
 }

 // validate the target version to make sure that it is not tampered in the UI
 var latestBuildTag = sn_edge_encryption.EdgeEncryptionAutoUpgrade.getLatestBuildTag();
 if (current.target_version != latestBuildTag) {
  gs.addInfoMessage(gs.getMessage("The target version must be {0}", latestBuildTag));
  current.setAbortAction(true);
  return;
 }

})(current, previous);
