x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    if (current.sold_product.account != current.install_base_item.account) {
        gs.addErrorMessage(gs.getMessage("Sold product and install base item are not selected from the same account"));
        current.setAbortAction(true);
    } else if (current.sold_product.consumer != current.install_base_item.consumer && current.sold_product.account.nil()) {
        gs.addErrorMessage(gs.getMessage("Sold product and install base item are not selected from the same consumer"));
        current.setAbortAction(true);
    } else if (GlidePluginManager.isActive('com.snc.service_organization') && current.sold_product.service_organization != current.install_base_item.service_organization) {
        gs.addErrorMessage(gs.getMessage("Sold product and install base item are not selected from the same service organization"));
        current.setAbortAction(true);
    } else if (GlidePluginManager.isActive('com.snc.household') && current.sold_product.household != current.install_base_item.household) {
        gs.addErrorMessage(gs.getMessage("Sold product and install base item are not selected from the same household"));
        current.setAbortAction(true);
    } else if (!gs.nil(current.sold_product.consumer_profile) && !gs.nil(current.install_base_item.consumer_profile) && current.sold_product.consumer_profile != current.install_base_item.consumer_profile) {
        gs.addErrorMessage(gs.getMessage("Sold product and install base item are not selected from the same consumer profile"));
        current.setAbortAction(true);
        return;
    }

    var installedProductGr = new GlideRecord('sn_install_base_m2m_installed_product');
    installedProductGr.addQuery('install_base_item', current.install_base_item.sys_id);
    installedProductGr.addQuery('sold_product', current.sold_product.sys_id);
    installedProductGr.query();
    if (installedProductGr.hasNext()) {
        gs.addErrorMessage(gs.getMessage("Sold product and install base item mapping already exists."));
        current.setAbortAction(true);
    }

})(current, previous);
