(function executeRule(current, previous) {
 var projectCurrency = current.cost_plan.project_currency;
 var functionalCurrency  = current.cost_plan.functional_currency;
 if (!projectCurrency) return;

 if (projectCurrency == functionalCurrency) {
  current.setValue(PmFieldConstants.COST_PROJECT_CURRENCY, current.cost_default_currency);
  current.setValue(PmFieldConstants.COST_EXCHANGE_RATE_PROJECT_CURRENCY, current.exchange_rate);
  current.setValue(PmFieldConstants.COST_REFERENCE_RATE_PROJECT_CURRENCY, current.fx_rate);
 } else if (projectCurrency == current.entered_currency) {
  current.setValue(PmFieldConstants.COST_PROJECT_CURRENCY, current.cost_local_currency);
  current.setValue(PmFieldConstants.COST_EXCHANGE_RATE_PROJECT_CURRENCY, "");
  current.setValue(PmFieldConstants.COST_REFERENCE_RATE_PROJECT_CURRENCY, "");
 } else {
  var fpService = new FinancialsForPPM();
        var exchangeRates = fpService.getExchangeRates([current.fiscal_period], current.entered_currency, current.getValue(PmFieldConstants.PROJECT_CURRENCY));
        if (exchangeRates.exchangeRatesSysIds.length) {
            current.setValue(PmFieldConstants.COST_REFERENCE_RATE_PROJECT_CURRENCY, exchangeRates.exchangeRatesSysIds[0]);
            current.setValue(PmFieldConstants.COST_EXCHANGE_RATE_PROJECT_CURRENCY , exchangeRates.exchangeRates[0]);
   current.exchange_rate_date = exchangeRates.exchangeRateDates[0];
            var currencyService = new SNC.FMCurrency();
            if (exchangeRates.exchangeRatesSysIds[0]){
                current.setValue(PmFieldConstants.COST_PROJECT_CURRENCY, currencyService.calculateAmount(current.cost_local_currency, exchangeRates.exchangeRatesSysIds[0]));
   }
            else {
                current.setValue(PmFieldConstants.COST_PROJECT_CURRENCY, current.cost_local_currency);
            }

        } else {
   current.setValue(PmFieldConstants.COST_PROJECT_CURRENCY, current.cost_local_currency);
   current.setValue(PmFieldConstants.COST_REFERENCE_RATE_PROJECT_CURRENCY, "");
            current.setValue(PmFieldConstants.COST_EXCHANGE_RATE_PROJECT_CURRENCY , "");
  }

 }
 if ((current.entered_currency == functionalCurrency) && (functionalCurrency == current.project_currency))
  current.exchange_rate_date = ""; 

})(current, previous);
